import Form from "./components/Form"

import './App.css';

class App extends Component {
  state={
    Price: undefined,
    Beta: undefined,
    VolAvg: undefined,
    MktCap: undefined,
    LastDiv: undefined,
    Range: undefined,
    Changes: undefined,
    ChangesPerc: undefined,
    companyName: undefined,
    exchange: undefined,
    industry: undefined,
    website: undefined,
    description: undefined,
    CEO:undefined,
  }
  getWeather=async(e)=>{
    e.preventDefault();
    const stockTicker=e.target.elements.country.value;
    const api_call = await fetch(`https://financialmodelingprep.com/api/company/profile/${stockTicker}`)
